import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import context from './context';

const TWELVE = 12;

function Provider({ children }) {
  const [foods, setFoods] = useState([]);
  // const [drinks, setDrinks] = useState([]);
  const [filters, setFilters] = useState({ filter: '', search: '' });

  useEffect(() => {
    const fetchApiFood = async () => {
      const response = await fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=');
      const data = await response.json();
      const foodList = data.meals.slice(0, TWELVE);
      setFoods(foodList);
    };
    fetchApiFood();
  }, []);

  useEffect(() => {
    let data;
    let newListFood;
    const fetchAPIFilters = async () => {
      switch (filters.filter) {
      case 'Ingredient':
        data = await fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?i=${filters.search}`);
        newListFood = await data.json();
        setFoods(newListFood.meals.slice(0, TWELVE));
        break;
      case 'Name':
        data = await fetch(`https://www.themealdb.com/api/json/v1/1/search.php?s=${filters.search}`);
        newListFood = await data.json();
        setFoods(newListFood.meals.slice(0, TWELVE));
        break;
      case 'First Letter':
        data = await fetch(`https://www.themealdb.com/api/json/v1/1/search.php?f=${filters.search}`);
        newListFood = await data.json();
        setFoods(newListFood.meals.slice(0, TWELVE));
        break;
      default:
        return '';
      }
    };
    fetchAPIFilters();
  }, [filters]);

  return (
    <context.Provider value={ { foods, setFilters } }>
      {children}
    </context.Provider>
  );
}

Provider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default Provider;
