import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';

const SIX = 6;

function Recommendation() {
  const history = useHistory();
  const [recipes, setRecipes] = useState([]);
  const [twoRecipes, setTwoRecipes] = useState([]);

  useEffect(() => {
    const fetchAPI = async () => {
      if (history.location.pathname.includes('/drinks')) {
        const response = await fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=');
        const data = await response.json();
        setRecipes(data.meals.slice(0, SIX));
        setTwoRecipes(data.meals.slice(0, 2));
      } else {
        const response = await fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=');
        const data = await response.json();
        setRecipes(data.drinks.slice(0, SIX));
        setTwoRecipes(data.drinks.slice(0, 2));
      }
    };
    fetchAPI();
  }, [history]);

  console.log(recipes);
  return (
    <div>
      <h2>Recommendations</h2>
      {recipes.length > 0 && (
        twoRecipes.map((recipe) => (
          <div key={ recipe.idMeal } style={ { display: 'flex' } }>
            <img src={ recipe.strMealThumb } alt={ recipe.strMeal } width="200px" />
          </div>
        ))
      )}
    </div>
  );
}

export default Recommendation;
