import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import copy from 'clipboard-copy';
import Recommendation from '../components/Recommendation';
import shareIcon from '../images/shareIcon.svg';
import whiteHeartIcon from '../images/whiteHeartIcon.svg';
import blackHeartIcon from '../images/blackHeartIcon.svg';

const THREE_MILLISECONDS = 3000;

function RecipesDetails() {
  const history = useHistory();
  const { pathname } = history.location;
  const id = pathname.split('/')[2];
  const [recipe, setRecipe] = useState({});
  const [isShare, setIsShare] = useState(false);
  const [isFave, setIsFave] = useState(false);

  useEffect(() => {
    const favorites = JSON.parse(localStorage.getItem('favoriteRecipes'));
    if (pathname.includes('/drinks') && favorites) {
      const isFavorite = favorites.some((favorite) => favorite.id === recipe.idDrink);
      if (isFavorite) setIsFave(true);
    } else if (favorites) {
      const isFavorite = favorites.some((favorite) => favorite.id === recipe.idMeal);
      if (isFavorite) setIsFave(true);
    }
  }, [pathname, recipe.idDrink, recipe.idMeal]);

  const setFavorites = (id) => {
    const favorites = JSON.parse(localStorage.getItem('favoriteRecipes'));
    if (favorites) {
      const isFavorite = favorites.some((favorite) => favorite.id === id);
      if (isFavorite) {
        setIsFave(false);
        const newFavorites = favorites.filter((favorite) => favorite.id !== id);
        localStorage.setItem('favoriteRecipes', JSON.stringify(newFavorites));
      } else {
        setIsFave(true);
        const newFavorites = [...favorites, save];
        localStorage.setItem('favoriteRecipes', JSON.stringify(newFavorites));
      }
    } else {
      setIsFave(true);
      localStorage.setItem('favoriteRecipes', JSON.stringify([save]));
    }
  };

  const saveRecipe = () => {
    if (pathname.includes('/drinks')) {
      const { idDrink, strAlcoholic, strCategory, strDrink, strDrinkThumb } = recipe;
      const save = {
        id: idDrink,
        type: 'drink',
        nationality: '',
        category: strCategory,
        alcoholicOrNot: strAlcoholic,
        name: strDrink,
        image: strDrinkThumb,
      };
      const favorites = JSON.parse(localStorage.getItem('favoriteRecipes'));
      if (favorites) {
        const isFavorite = favorites.some((favorite) => favorite.id === idDrink);
        if (isFavorite) {
          setIsFave(false);
          const newFavorites = favorites.filter((favorite) => favorite.id !== idDrink);
          localStorage.setItem('favoriteRecipes', JSON.stringify(newFavorites));
        } else {
          setIsFave(true);
          const newFavorites = [...favorites, save];
          localStorage.setItem('favoriteRecipes', JSON.stringify(newFavorites));
        }
      } else {
        setIsFave(true);
        localStorage.setItem('favoriteRecipes', JSON.stringify([save]));
      }
    } else {
      const { idMeal, strArea, strCategory, strMeal, strMealThumb } = recipe;
      const save = {
        id: idMeal,
        type: 'food',
        nationality: strArea,
        category: strCategory,
        alcoholicOrNot: '',
        name: strMeal,
        image: strMealThumb,
      };
      const favorites = JSON.parse(localStorage.getItem('favoriteRecipes'));
      if (favorites) {
        const isFavorite = favorites.some((favorite) => favorite.id === idMeal);
        if (isFavorite) {
          setIsFave(false);
          const newFavorites = favorites.filter((favorite) => favorite.id !== idMeal);
          localStorage.setItem('favoriteRecipes', JSON.stringify(newFavorites));
        } else {
          setIsFave(true);
          const newFavorites = [...favorites, save];
          localStorage.setItem('favoriteRecipes', JSON.stringify(newFavorites));
        }
      } else {
        setIsFave(true);
        localStorage.setItem('favoriteRecipes', JSON.stringify([save]));
      }
    }
  };

  const copyLink = () => {
    copy(window.location.href);
    setIsShare(true);
    setTimeout(() => setIsShare(false), THREE_MILLISECONDS);
  };

  useEffect(() => {
    const fetchApiById = async () => {
      if (pathname.includes('/drinks')) {
        const response = await fetch(`https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${id}`);
        const data = await response.json();
        setRecipe(data.drinks[0]);
      } else {
        const response = await fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${id}`);
        const data = await response.json();
        setRecipe(data.meals[0]);
      }
    };
    fetchApiById();
  }, [id, pathname, setRecipe]);

  if (pathname.includes('/drinks')) {
    const { strDrinkThumb, strDrink,
      strInstructions, strAlcoholic } = recipe;
    const listRecipe = Object.entries(recipe);
    const listAllIngredients = listRecipe
      .filter((arr) => arr[0].includes('strIngredient'));
    const listIngredients = listAllIngredients.filter((arr) => arr[1]);
    const ingredients = listIngredients.reduce((acc, arr) => {
      acc = [...acc, arr[1]];
      return acc;
    }, []);

    const listAllMeasure = listRecipe
      .filter((arr) => arr[0].includes('strMeasure'));
    const listMeasure = listAllMeasure.filter((arr) => arr[1]);
    const measures = listMeasure.reduce((acc, arr) => {
      acc = [...acc, arr[1]];
      return acc;
    }, []);

    return (
      <div>
        <img
          src={ strDrinkThumb }
          alt={ strDrink }
          data-testid="recipe-photo"
          width="200px"
        />
        <h2 data-testid="recipe-title">{strDrink}</h2>
        <button
          type="button"
          data-testid="share-btn"
          onClick={ copyLink }
        >
          <img src={ shareIcon } alt="shareIcon" />
        </button>
        {isShare && <span>Link copied!</span>}
        <button
          type="button"
          data-testid="favorite-btn"
          onClick={ saveRecipe }
        >
          <img src={ isFave ? blackHeartIcon : whiteHeartIcon } alt="whiteHeartIcon" />
        </button>
        <p data-testid="recipe-category">{strAlcoholic}</p>
        {ingredients.map((ingredient, index) => (
          <p
            data-testid={ `${index}-ingredient-name-and-measure` }
            key={ ingredient }
          >
            {`${ingredient}  ${measures[index] === undefined
              ? '' : `- ${measures[index]}`}`}
          </p>
        ))}
        <p data-testid="instructions">{strInstructions}</p>
        <Recommendation />
      </div>
    );
  }

  const { strMealThumb, strMeal, strCategory, strInstructions, strYoutube } = recipe;
  const listRecipe = Object.entries(recipe);
  const listAllIngredients = listRecipe
    .filter((arr) => arr[0].includes('strIngredient'));
  const listIngredients = listAllIngredients.filter((arr) => arr[1]);
  const ingredients = listIngredients.reduce((acc, arr) => {
    acc = [...acc, arr[1]];
    return acc;
  }, []);

  const listAllMeasure = listRecipe
    .filter((arr) => arr[0].includes('strMeasure'));
  const listMeasure = listAllMeasure.filter((arr) => arr[1]);
  const measures = listMeasure.reduce((acc, arr) => {
    acc = [...acc, arr[1]];
    return acc;
  }, []);

  const linkYoutube = strYoutube?.replace('watch?v=', 'embed/');

  return (
    <div>
      <img
        src={ strMealThumb }
        alt={ strMeal }
        data-testid="recipe-photo"
        width="200px"
      />
      <h2 data-testid="recipe-title">{strMeal}</h2>
      <button
        type="button"
        data-testid="share-btn"
        onClick={ copyLink }
      >
        <img src={ shareIcon } alt="shareIcon" />
      </button>
      {isShare && <span>Link copied!</span>}
      <button type="button" data-testid="favorite-btn" onClick={ saveRecipe }>
        <img src={ isFave ? blackHeartIcon : whiteHeartIcon } alt="whiteHeartIcon" />
      </button>
      <h3 data-testid="recipe-category">{strCategory}</h3>
      {ingredients.map((ingredient, index) => (
        <p
          data-testid={ `${index}-ingredient-name-and-measure` }
          key={ ingredient }
        >
          {`${ingredient}  ${measures[index] === undefined
            ? '' : `- ${measures[index]}`}`}
        </p>
      ))}
      <iframe
        data-testid="video"
        width="360"
        height="315"
        src={ linkYoutube }
        title="YouTube video player"
        frameBorder="0"
        allow="accelerometer;
         autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowFullScreen
      />
      <p data-testid="instructions">{strInstructions}</p>
      <Recommendation />
    </div>
  );
}

export default RecipesDetails;
