import React, { useEffect, useState } from 'react';
import Header from '../components/Header';

function DoneRecipes() {
  const [recipeFinished, setRecipeFinished] = useState([]);

  useEffect(() => {
    const finished = JSON.parse(localStorage.getItem('doneRecipes'));
    if (finished) {
      setRecipeFinished(finished);
    }
  }, [setRecipeFinished]);

  const copyLink = () => {
    const linkURL = window.location.href;
    const newLinkURL = linkURL.replace('/in-progress', '');
    copy(newLinkURL);
    setIsShare(true);
    setTimeout(() => setIsShare(false), THREE_MILLISECONDS);
  };

  return (
    <>
      <Header title="Done Recipes" isSearch={ false } />
      <button type="button" data-testid="filter-by-all-btn">All</button>
      <button type="button" data-testid="filter-by-food-btn">Foods</button>
      <button type="button" data-testid="filter-by-drink-btn">Drinks</button>
      {recipeFinished.length > 0 && (
        recipeFinished.map((recipe, index) => {
          const { id, type, nationality, category,
            alcoholicOrNot, name, image, doneDate, tags } = recipe;
          return (
            <div key={ `${index}-${name}` }>
              <img
                src={ image }
                alt={ name }
                data-testid={ `${index}-horizontal-image` }
              />
              <h2 data-testid={ `${index}-horizontal-name` }>{name}</h2>
              <p data-testid={ `${index}-horizontal-top-text` }>{category}</p>
              <p data-testid={ `${index}-horizontal-done-date` }>{doneDate}</p>
              <p data-testid={ `${index}-${tagName}-horizontal-tag` }>{tags}</p>
              <button
                type="button"
                data-testid="share-btn"
                onClick={ copyLink }
              >
                <img src={ shareIcon } alt="shareIcon" />
              </button>
            </div>
          );
        })
      )}
    </>
  );
}

export default DoneRecipes;
