import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import InteractionButtons from '../components/InteractionButtons';

function RecipeInProgress() {
  const [recipe, setRecipe] = useState({});
  const history = useHistory();
  const { pathname } = history.location;
  const id = pathname.split('/')[2];
  const [isFinish, setIsFinish] = useState(true);

  const markedIngredients = (page) => {
    const recipesInProgress = JSON.parse(localStorage.getItem('inProgressRecipes'));
    const listRecipe = Object.entries(recipe);
    const listAllIngredients = listRecipe
      .filter((arr) => arr[0].includes('strIngredient'));
    const listIngredients = listAllIngredients.filter((arr) => arr[1]);
    const ingredients = listIngredients.reduce((acc, arr) => {
      acc = [...acc, arr[1]];
      return acc;
    }, []);
    console.log(ingredients);
    console.log(recipesInProgress[page][id]);
    const allIngredientsMarked = recipesInProgress[page][id] === ingredients;

    ingredients.forEach((ing, index) => {
      if (ing === recipesInProgress[page][id][index]) {
        setIsFinish(false);
      } else {
        setIsFinish(true);
      }
    });

    console.log(allIngredientsMarked);
  };

  const saveProgress = (ing, page) => {
    const recipesInProgress = JSON.parse(localStorage.getItem('inProgressRecipes'));
    if (recipesInProgress[page][id]) {
      const isSaveIngredient = recipesInProgress[page][id].some((i) => i === ing);
      if (isSaveIngredient) {
        const newRecipesInProgress = {
          ...recipesInProgress,
          [page]: {
            ...recipesInProgress[page],
            [id]: recipesInProgress[page][id].filter((i) => i !== ing),
          },
        };
        localStorage.setItem('inProgressRecipes', JSON.stringify(newRecipesInProgress));
        markedIngredients(page);
      } else {
        const newRecipesInProgress = {
          ...recipesInProgress,
          [page]: {
            ...recipesInProgress[page],
            [id]: [...recipesInProgress[page][id], ing],
          },
        };
        localStorage.setItem('inProgressRecipes', JSON.stringify(newRecipesInProgress));
        markedIngredients(page);
      }
    } else {
      const newRecipesInProgress = {
        ...recipesInProgress,
        [page]: {
          ...recipesInProgress[page],
          [id]: [ing],
        },
      };
      localStorage.setItem('inProgressRecipes', JSON.stringify(newRecipesInProgress));
      markedIngredients(page);
    }
  };

  useEffect(() => {
    const fetchApiById = async () => {
      if (pathname.includes('/drinks')) {
        const response = await fetch(`https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${id}`);
        const data = await response.json();
        setRecipe(data.drinks[0]);
      } else {
        const response = await fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${id}`);
        const data = await response.json();
        setRecipe(data.meals[0]);
      }
    };
    fetchApiById();
  }, [id, pathname, setRecipe]);

  if (pathname.includes('/drinks')) {
    const { strDrinkThumb, strDrink, strAlcoholic, strInstructions } = recipe;

    const listRecipe = Object.entries(recipe);
    const listAllIngredients = listRecipe
      .filter((arr) => arr[0].includes('strIngredient'));
    const listIngredients = listAllIngredients.filter((arr) => arr[1]);
    const ingredients = listIngredients.reduce((acc, arr) => {
      acc = [...acc, arr[1]];
      return acc;
    }, []);

    return (
      <div>
        <img
          src={ strDrinkThumb }
          alt={ strDrink }
          data-testid="recipe-photo"
          width="200px"
        />
        <h2 data-testid="recipe-title">{strDrink}</h2>
        <InteractionButtons recipe={ recipe } />
        <p data-testid="recipe-category">{strAlcoholic}</p>
        <div style={ { display: 'flex', flexDirection: 'column' } }>
          {ingredients.map((ingredient, index) => (
            <label
              key={ `${index}-ingredient` }
              htmlFor={ ingredient }
              data-testid={ `${index}-ingredient-step` }
              onChange={ () => saveProgress(ingredient, 'cocktails') }
            >
              <input
                type="checkbox"
                name={ ingredient }
                id={ ingredient }
              />
              {ingredient}
            </label>
          ))}
        </div>
        <p data-testid="instructions">{strInstructions}</p>
        <footer
          style={ { position: 'fixed', width: '100%', bottom: 0 } }
        >
          <button type="button" data-testid="finish-recipe-btn" disabled={ isFinish }>
            Finish Recipe
          </button>
        </footer>
      </div>
    );
  }

  const { strMealThumb, strMeal, strCategory, strInstructions } = recipe;
  const listRecipe = Object.entries(recipe);
  const listAllIngredients = listRecipe
    .filter((arr) => arr[0].includes('strIngredient'));
  const listIngredients = listAllIngredients.filter((arr) => arr[1]);
  const ingredients = listIngredients.reduce((acc, arr) => {
    acc = [...acc, arr[1]];
    return acc;
  }, []);

  return (
    <div>
      <img
        src={ strMealThumb }
        alt={ strMeal }
        data-testid="recipe-photo"
        width="200px"
      />
      <h2 data-testid="recipe-title">{strMeal}</h2>
      <InteractionButtons recipe={ recipe } />
      <h3 data-testid="recipe-category">{strCategory}</h3>
      <div style={ { display: 'flex', flexDirection: 'column' } }>
        {ingredients.map((ingredient, index) => (
          <label
            key={ `${index}-ingredient` }
            htmlFor={ ingredient }
            data-testid={ `${index}-ingredient-step` }
            onChange={ () => saveProgress(ingredient, 'meals') }
          >
            <input type="checkbox" name={ ingredient } id={ ingredient } />
            {ingredient}
          </label>
        ))}
      </div>
      <p data-testid="instructions">{strInstructions}</p>
      <footer
        style={ { position: 'fixed', width: '100%', bottom: 0 } }
      >
        <button type="button" data-testid="finish-recipe-btn">
          Finish Recipe
        </button>
      </footer>
    </div>
  );
}

export default RecipeInProgress;
