import React, { useContext, useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { useHistory } from 'react-router-dom';
import context from '../context/context';

const FIVE = 5;
const TWELVE = 12;

function Categorys({ url, category }) {
  const [filters, setFilters] = useState([]);
  const history = useHistory();
  const { setDrinks, setFoods, foods, drinks } = useContext(context);
  const [isFilter, setIsFilter] = useState(false);

  useEffect(() => {
    const fetchApiCategorys = async () => {
      const response = await fetch(url);
      const data = await response.json();
      const newData = data[category].slice(0, FIVE);
      setFilters(newData);
    };
    return fetchApiCategorys();
  }, [url, category]);

  const fetchCategorys = async (urlCategory, category) => {
    if (category === 'drinks') {
      const response = await fetch(urlCategory);
      const data = await response.json();
      const newData = data.drinks.slice(0, TWELVE);
      console.log(newData);
      // setDrinks();
      return data;
    }
    // setFoods();

    console.log(urlCategory, category);
    return urlCategory;
  };

  const filterCategory = (strCategory) => {
    setIsFilter(!isFilter);
    const { pathname } = history.location;
    const newCategory = strCategory.replace(/ /g, '_');
    if (pathname === '/drinks') {
      fetchCategorys(`https://www.thecocktaildb.com/api/json/v1/1/filter.php?c=${newCategory}`,
        'drinks');
    } else {
      fetchCategorys(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${strCategory}`,
        'foods');
    }
  };

  return (
    <>
      <button
        type="button"
        data-testid="All-category-filter"
        onClick={ () => filterCategory('All') }
      >
        All
      </button>
      {filters.map((filter) => {
        const { strCategory } = filter;
        return (
          <button
            type="button"
            key={ strCategory }
            data-testid={ `${strCategory}-category-filter` }
            onClick={ () => filterCategory(strCategory) }
          >
            {strCategory}
          </button>
        );
      })}
    </>
  );
}

Categorys.propTypes = {
  url: PropTypes.string.isRequired,
  category: PropTypes.string.isRequired,
};

export default Categorys;
