import React, { useContext, useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import copy from 'clipboard-copy';
import Header from '../components/Header';
import context from '../context/context';
import shareIcon from '../images/shareIcon.svg';

function FavoriteRecipes() {
  const [faveRecipes, setFaveRecipes] = useState([]);
  const [allFaveRecipes, setAllFaveRecipes] = useState([]);
  const [isShare, setIsShare] = useState(false);
  const history = useHistory();
  const { isFave } = useContext(context);

  useEffect(() => {
    setFaveRecipes(JSON.parse(localStorage.getItem('favoriteRecipes')));
    setAllFaveRecipes(JSON.parse(localStorage.getItem('favoriteRecipes')));
  }, [setFaveRecipes, isFave]);

  const filter = (fil) => {
    if (fil === 'drinks') {
      const newList = allFaveRecipes.filter((recipe) => recipe.type === 'drink');
      setFaveRecipes(newList);
    } else if (fil === 'foods') {
      const newList = allFaveRecipes.filter((recipe) => recipe.type === 'food');
      setFaveRecipes(newList);
    } else {
      setFaveRecipes(allFaveRecipes);
    }
  };

  const copyLink = (recipe) => {
    const { id, type } = recipe;
    const linkURL = window.location.href;
    const newLinkURL = linkURL.replace('/done-recipes', `/${type}s/${id}`);
    copy(newLinkURL);
    setIsShare(true);
    setTimeout(() => setIsShare(false), THREE_MILLISECONDS);
  };

  return (
    <>
      {console.log(faveRecipes)}
      <Header title="Favorite Recipes" isSearch={ false } />
      {faveRecipes?.length > 0 && (
        <div>
          <button
            type="button"
            data-testid="filter-by-all-btn"
            onClick={ () => filter('all') }
          >
            All
          </button>
          <button
            type="button"
            data-testid="filter-by-food-btn"
            onClick={ () => filter('foods') }
          >
            Foods
          </button>
          <button
            type="button"
            data-testid="filter-by-drink-btn"
            onClick={ () => filter('drinks') }
          >
            Drinks
          </button>
        </div>
      )}
      {faveRecipes?.length > 0 && (
        faveRecipes.map((recipe, index) => {
          const { id, category, name, image, nationality, type, alcoholicOrNot } = recipe;
          return (
            <div key={ `${index}-${name}` }>
              <button
                onClick={ () => {
                  if (type === 'drink') {
                    history.push(`/drinks/${id}`);
                  } else {
                    history.push(`/foods/${id}`);
                  }
                } }
                type="button"
              >
                <img
                  src={ image }
                  alt={ name }
                  data-testid={ `${index}-horizontal-image` }
                  width="200px"
                />
                <h2 data-testid={ `${index}-horizontal-name` }>{name}</h2>
              </button>
              <p data-testid={ `${index}-horizontal-top-text` }>
                {type === 'drink' ? alcoholicOrNot : `${nationality} - ${category}`}
              </p>
              <button
                type="button"
                data-testid={ `${index}-horizontal-share-btn` }
                onClick={ () => copyLink(recipe) }
                src={ shareIcon }
              >
                <img src={ shareIcon } alt="shareIcon" />
              </button>
              {isShare && <span>Link copied!</span>}
            </div>
          );
        })
      )}
    </>
  );
}

export default FavoriteRecipes;
