import React, { useContext, useState } from 'react';
import context from '../context/context';

function SearchBar() {
  const { setFilters } = useContext(context);
  const [ingredients, setIngredients] = useState(true);
  const [name, setName] = useState(false);
  const [firstLetter, setFirstLetter] = useState(false);

  return (
    <div>
      <label htmlFor="searchBar">
        <input
          id="searchBar"
          type="text"
          placeholder="Search..."
          data-testid="search-input"
        />
      </label>

      <label htmlFor="radioIndredients">
        <input
          id="radioIndredients"
          name="search"
          type="radio"
          data-testid="ingredient-search-radio"
          checked={ ingredients }
          onClick={ () => {
            setIngredients(true);
            setName(false);
            setFirstLetter(false);
          } }
        />
        Ingredients
      </label>

      <label htmlFor="radioName">
        <input
          id="radioName"
          type="radio"
          name="search"
          data-testid="name-search-radio"
          checked={ name }
          onClick={ () => {
            setName(true);
            setIngredients(false);
            setFirstLetter(false);
          } }
        />
        Name
      </label>

      <label htmlFor="radioFisrt">
        <input
          type="radio"
          name="search"
          id="radioFisrt"
          data-testid="first-letter-search-radio"
          checked={ firstLetter }
          onClick={ () => {
            setFirstLetter(true);
            setName(false);
            setIngredients(false);
          } }
        />
        First Letter
      </label>

      <button type="button" data-testid="exec-search-btn">
        Search
      </button>
    </div>
  );
}

export default SearchBar;
