import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import context from './context';

const TWELVE = 12;

const error = 'Sorry, we haven\'t found any recipes for these filters.';

function Provider({ children }) {
  const [drinkPage, setDrinksPage] = useState(false);
  const urlDefault = drinkPage ? 'https://www.thecocktaildb.com/api/json/v1/1/search.php?s='
    : 'https://www.themealdb.com/api/json/v1/1/search.php?s=';
  const [foods, setFoods] = useState([]);
  const [filters, setFilters] = useState({ filter: '', search: '' });
  const [url, setUrl] = useState(urlDefault);

  // const [drinks, setDrinks] = useState([]);

  useEffect(() => {
    const fetchApiFood = async () => {
      const response = await fetch(url);
      const data = await response.json();
      if (data.meals === null) {
        global.alert(error);
        return null;
      }
      const foodList = data.meals.slice(0, TWELVE);
      setFoods(foodList);
    };
    fetchApiFood();
  }, [url]);

  useEffect(() => {
    const fetchAPIFilters = () => {
      switch (filters.filter) {
      case 'Ingredient':
        if (filters.search === 'chicken') {
          setUrl('https://www.themealdb.com/api/json/v1/1/filter.php?i=chicken');
          break;
        }
        setUrl(`https://www.themealdb.com/api/json/v1/1/filter.php?i=${filters.search}`);
        break;
      case 'Name':
        setUrl(`https://www.themealdb.com/api/json/v1/1/search.php?s=${filters.search}`);
        break;
      case 'First Letter':
        setUrl(`https://www.themealdb.com/api/json/v1/1/search.php?f=${filters.search}`);
        break;
      default:
        return null;
      }
    };
    fetchAPIFilters();
  }, [filters]);

  // useEffect(() => {
  //   const fetchAPIFilters = () => {
  //     switch (filters.filter) {
  //     case 'Ingredient':
  //       if (filters.search === 'chicken') {
  //         setUrl('https://www.themealdb.com/api/json/v1/1/filter.php?i=chicken');
  //         break;
  //       }
  //       setUrl(`https://www.themealdb.com/api/json/v1/1/filter.php?i=${filters.search}`);
  //       break;
  //     case 'Name':
  //       setUrl(`https://www.themealdb.com/api/json/v1/1/search.php?s=${filters.search}`);
  //       break;
  //     case 'First Letter':
  //       setUrl(`https://www.themealdb.com/api/json/v1/1/search.php?f=${filters.search}`);
  //       break;
  //     default:
  //       return null;
  //     }
  //   };
  //   fetchAPIFilters();
  // }, [filters]);

  return (
    <context.Provider value={ { foods, setFilters, setDrinksPage } }>
      {children}
    </context.Provider>
  );
}

Provider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default Provider;
